    _s.linkTrackVars = 'events,eVar45,prop4,prop19';
    _s.linkTrackEvents = 'event107';
    _s.events = 'event107';
    _s.eVar45 = 'check store stock';
    _s.prop4 = 'plp';
    _s.prop19 = _s.eVar45;

