    		var clickedElement = $(e.target),
                $elem = $(this),
                tooltipPopup = $elem.parent().children('.tooltipPopup'),
                tooltipLeft = $(this).width() + 45 + "px",
                tooltipPos = "-" + ($(this).offset().top - (tooltipPopup.height() / 2) + 20 + "px");
    		
